INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (1, 0, 1, 1, 'en', 'New Registration Email to User', 'New Registration Email to User', 'Thank you for your Registration on %#SOD_PROGRAM_NAME#%', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\"/></td>\r\n		</tr>\r\n		 \r\n		<tr>\r\n			<td align=\"left\" style=\"padding:10px;\">\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Thank you for registering. We will now review your registration. A confirmation email will be sent to the e-mail address you provided once an administrator has approved your registration.</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Kind regards,<br />\r\n			<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1628676169);
INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (2, 0, 2, 1, 'en', 'Password Reset', 'Password Reset Email', 'Reset your password with %#SOD_PROGRAM_NAME#%.', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\" /></td>\r\n		</tr>\r\n		 \r\n		<tr>\r\n			<td align=\"left\" style=\"padding:10px;\">\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Please follow the link below (or copy the link directly into the address bar of your browser) to reset your password with %#SOD_PROGRAM_NAME#%.</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\"><a href=\"%#PASSWORD_RESET_LINK#%\">Reset your password.</a></font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\"><font face=\"Poppins, Arial\" size=\"2\">Kind regards,<br />\r\n			<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1628676189);
INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (3, 0, 3, 2, 'en', 'On Reseller User Approval', 'Reseller User Approved Email', 'Your registration with %#SOD_PROGRAM_NAME#% has been approved.', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\" /></td>\r\n		</tr>\r\n		 \r\n		<tr>\r\n			<td align=\"left\" style=\"padding:10px;\">\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Congratulations! Your registration with our social media website - %#SOD_PROGRAM_NAME#% - has been approved.</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Please login via the url: <a href=\"%#LOGIN_URL#%\">%#LOGIN_URL#%</a> and follow the simple steps below to finalise your account settings.</font></p>\r\n\r\n			<ul>\r\n				<li><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Add your social media account(s)</font></li>\r\n				<li><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Set your content filters associated with each social media account</font></li>\r\n				<li><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Set your notification preferences to receive content as and when we make it available</font></li>\r\n			</ul>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">We hope you enjoy this innovative and powerful social media solution.</font></p>\r\n\r\n			<restrict data-has_am=\"y\">\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Your account manager is %#ACCOUNT_MANAGER#%.</font></p>\r\n			</restrict>\r\n\r\n			<p><font face=\"Poppins, Arial\" size=\"2\">Kind regards,<br />\r\n			<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1628676117);
INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (4, 0, 4, 2, 'en', 'On Reseller User Deletion', 'Reseller Deleted User Email', 'Your %#SOD_PROGRAM_NAME#% registration has been rejected.', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\" /></td>\r\n		</tr>\r\n		 \r\n		<tr>\r\n			<td align=\"left\" style=\"padding:10px;\">\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">We have not approved your registration with %#SOD_PROGRAM_NAME#%, and as such, you will be unable to access the site.</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">The reason given for this rejection is:</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">%#DELETION_REASON#%</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial\" size=\"2\">Kind regards,<br />\r\n		<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1628676136);
