INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (6, 0, 6, 1, 'en', 'Referral Email', 'Referral Email', 'You have been referred by a colleague to use %#SOD_PROGRAM_NAME#%.', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			<tr>\r\n				<td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\"/></td>\r\n		   </tr>\r\n			<td align=\"left\" style=\"padding:10px;\">\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">You have been referred by %#REFFERER_FIRST_NAME#% %#REFFERER_LAST_NAME#% to use our innovative and powerful social media solution - socialondemand.</font></p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<blockquote style=\"border-left: 3px solid gray; padding: 5px 15px;\"><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">%#REFFERER_MESSAGE%#</font></blockquote>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">To learn more about how to drive high value, targeted, instant information directly to your customers and prospects via your social media account or to register, simply click here:</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\"><a href=\"%#REGISTRATION_URL#%\">%#REGISTRATION_URL#%</a>.</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial\" size=\"2\">Kind regards,<br />\r\n		<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1628676216);
INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (7, 0, 7, 3, 'en', 'Upon Account Delegatation', 'Account delegation confirmation email', 'One of your company’s social media accounts has been delegated to you.', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\" ></td>\r\n		</tr>\r\n		\r\n		<tr>\r\n			<td align=\"left\" style=\"padding-right:5px;padding-left:5px;padding-top:5px;padding-bottom:15px; font-family:Arial, Helvetica, sans-serif; font-size:12px;\">\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">You have been delegated to manage posts on the social media account named %#ACCOUNT_FULL_NAME#% by %#REFFERER_FIRST_NAME#% %#REFFERER_LAST_NAME#%.</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">You now have the ability to run the account and to post or reject new content.</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">To log into the account please click here <a href=\"%#LOGIN_URL#%\">%#LOGIN_URL#%</a>.</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial, Helvetica, sans-serif\" size=\"2\">You can edit your email notification preferences to make sure you are notified of new content as and when you require.</font></p>\r\n\r\n			<p><font face=\"Poppins, Arial\" size=\"2\">Kind regards,<br>\r\n			<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%\r\n', 1, 1641552567);
