INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (12, 1, 12, 2, 'en', 'On Vendor User Approval', 'Vendor User Approved Email', 'Your registration with %#SOD_PROGRAM_NAME#% has been approved.', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\" /></td>\r\n		</tr>\r\n		 \r\n		<tr>\r\n			<td align=\"left\" style=\"padding:10px;\">\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Congratulations! Your registration with %#SOD_PROGRAM_NAME#% has been approved. You can login at:</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\"><a href=\"%#VENDOR_LOGIN_URL#%\">%#VENDOR_LOGIN_URL#%</a></font></p>\r\n\r\n			<p><font face=\"Arial\" size=\"2\">Kind regards,<br />\r\n			<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1628672224);
INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (13, 1, 13, 2, 'en', 'On Vendor User Deletion', 'Vendor Deleted User Email', 'Your %#SOD_PROGRAM_NAME#% registration has been rejected.', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\" /></td>\r\n		</tr>\r\n		 \r\n		<tr>\r\n			<td align=\"left\" style=\"padding:10px;\">\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">We have not approved your registration with %#SOD_PROGRAM_NAME#%, and as such, you will be unable to access the site.</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">The reason given for this rejection is:</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">%#DELETION_REASON#%</font></p>\r\n\r\n			<p><font face=\"Arial\" size=\"2\">Kind regards,<br />\r\n			<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1628676064);
