INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (700, 0, 700, 3, 'en', 'If an account is found to be expired', 'Urgent action required for your LinkedIn account(s)', 'Urgent action required for your LinkedIn account(s)', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\"></td>\r\n		</tr>\r\n		\r\n		<tr>\r\n			<td align=\"left\" style=\"padding-right:5px;padding-left:5px;padding-top:5px;padding-bottom:10px; font-family:Arial, Helvetica, sans-serif; font-size:12px;\">\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">We have noticed that your LinkedIn account(s), shown below, will soon expire or has/have already expired:</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">%#ACCOUNTS_LIST#%</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Please <a href=\"%#SITE_DOMAIN#%/reseller/\">login to the portal</a> to re-authenticate your account(s), so you can continue to post via LinkedIn.</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Kind regards,<br>\r\n			<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1641552732);
INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (800, 1, 800, 3, 'en', 'When a Draft has been shared', 'Vendor Post Draft Shared Email', 'New social media content has been shared with you', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\"></td>\r\n		</tr>\r\n		\r\n		<tr>\r\n			<td align=\"left\" style=\"padding-right:5px;padding-left:5px;padding-top:5px;padding-bottom:10px; font-family:Arial, Helvetica, sans-serif; font-size:12px;\">\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">%#SHARER_FIRST_NAME#% %#SHARER_LAST_NAME#% has shared new social media content with you.</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">You can now review / edit and send this content for review.</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">To view this content please go to your: <a href=\"%#SITE_DOMAIN#%/vendor/socialmedia/postdrafts/\">Social Media Draft Posts page</a>.</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Kind regards,<br>\r\n			<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1641552745);
