INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (1109, 0, 1109, 2, 'en', 'User has been approved, notifying account manager', 'A new partner has been approved.', 'A new partner has been approved.', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\"/></td>\r\n		</tr>\r\n		 \r\n		<tr>\r\n			<td align=\"left\" style=\"padding:10px;\">\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">%#USER_NAME#% from %#COMPANY_NAME#% has been approved by %#APPROVER_NAME#% as a %#PARTNER_TYPE#% in the&nbsp;<span style=\"font-family: Arial, Helvetica, sans-serif; font-size: small;\">#PROGRAM#</span>. <a href=\"%#PROFILE_LINK#%\">View the details here</a>.</font></p>\r\n\r\n			<restrict data-has_am=\"y\">\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">You are the named account manager for this company.</font></p>\r\n			</restrict>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Kind regards,<br />\r\n			<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1628676090);
