INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (1467, 0, 1467, 3, 'en', 'If an account is found to be expired', 'Urgent action required for your social media account(s)', 'Urgent action required for your social media account(s)', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\"></td>\r\n		</tr>\r\n		\r\n		<tr>\r\n			<td align=\"left\" style=\"padding-right:5px;padding-left:5px;padding-top:5px;padding-bottom:10px; font-family:Arial, Helvetica, sans-serif; font-size:12px;\">\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Dear %#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">We have noticed that some of your social media accounts will soon expire or have already expired.</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Please login to <a href=\"%#SITE_DOMAIN#%/user/socialmedia/add/?email=y\">%#SOD_PROGRAM_NAME#%</a> to re-authenticate your account(s), so you can continue to share content.</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Kind regards,<br>\r\n			<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1641552802);
