INSERT INTO `emails_emailscontent`(`ID`, `isVendor`, `topID`, `moduleID`, `langCode`, `trigger`, `title`, `subject`, `htmlContent`, `userID`, `lastUpdate`) VALUES (1490, 1, 1490, 4, 'en', 'Article Approval Notification', 'Article Approval Notification', 'An article has been approved', '<table align=\"center\" cellpadding=\"0\" cellspacing=\"0\" style=\"border:1px solid #999; background-color:#fff;\" width=\"600\">\r\n	<tbody>\r\n		<tr>\r\n			 <td height=\"50\" style=\"padding: 15px;\"><img src=\"##logoUrl##\" height=\"##logoHeight##\" width=\"##logoWidth##\" /></td>\r\n		</tr>\r\n		 \r\n		<tr>\r\n			<td align=\"left\" style=\"padding:10px;\">\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Dear</font> <font face=\"Arial, Helvetica, sans-serif\" size=\"2\">%#TO_USER_FIRST_NAME#%,</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">An article that was sent for&nbsp;translation has been approved.</font></p>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Please <a href=\"%#QUEUE_URL#%\"><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">click here</font></a> to view the article in the queue.</font></p>\r\n\r\n			<table width=\"100%\">\r\n				<tbody>\r\n					<tr>\r\n						<td width=\"170\"><font face=\"Arial, Helvetica, sans-serif\" size=\"2\"><strong>Article Title:</strong></font></td>\r\n						<td width=\"400\"><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">%#ARTICLE_TITLE#%</font></td>\r\n					</tr>\r\n					<tr>\r\n						<td><font face=\"Arial, Helvetica, sans-serif\" size=\"2\"><strong>Created By:</strong></font></td>\r\n						<td><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">%#ARTICLE_CREATOR#%</font></td>\r\n					</tr>\r\n					<tr>\r\n						<td><font face=\"Arial, Helvetica, sans-serif\" size=\"2\"><strong>Time Created:</strong></font></td>\r\n						<td><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">%#CREATION_TIME#%</font></td>\r\n					</tr>\r\n					<tr>\r\n						<td><font face=\"Arial, Helvetica, sans-serif\" size=\"2\"><strong>Time Scheduled to Send:</strong></font></td>\r\n						<td><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">%#SCHEDULED_TIME#%</font></td>\r\n					</tr>\r\n					<tr>\r\n						<td colspan=\"2\">\r\n						<hr /></td>\r\n					</tr>\r\n					<tr>\r\n						<td colspan=\"2\"><font face=\"Arial, Helvetica, sans-serif\" size=\"2\"><strong>Short Article:</strong></font></td>\r\n					</tr>\r\n					<tr>\r\n						<td colspan=\"2\"><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">%#SHORT_ARTICLE#%</font></td>\r\n					</tr>\r\n					<tr>\r\n						<td colspan=\"2\">\r\n						<hr /></td>\r\n					</tr>\r\n					<tr>\r\n						<td colspan=\"2\"><font face=\"Arial, Helvetica, sans-serif\" size=\"2\"><strong>Long Article:</strong></font></td>\r\n					</tr>\r\n					<tr>\r\n						<td colspan=\"2\"><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">%#LONG_ARTICLE#%</font></td>\r\n					</tr>\r\n					<tr>\r\n						<td colspan=\"2\">\r\n						<hr /></td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n\r\n			<p><font face=\"Arial, Helvetica, sans-serif\" size=\"2\">Kind regards,<br />\r\n			<strong>%#SOD_PROGRAM_NAME#%</strong></font></p>\r\n			</td>\r\n		</tr>\r\n		\r\n	</tbody>\r\n</table>\r\n%#FOOTER_LINKS#%', 1, 1628674590);
